export const BtnMusica = () => {
  const [imgS, setImgS] = useState(img_acceso);
  const [imgD, setImgD] = useState(img_spento);

  const {music, setMusic} = useContext(Music);

  const premiOn = () => {
    setImgS(img_acceso_on);
    setImgD(img_spento);
    setMusic();
  };

  const premiOff = () => {
    setImgS(img_acceso);
    setImgD(img_spento_on);
    setMusic();
  };

  useEffect(() => {
   if (music == true){
    setImgS(img_acceso_on);
    setImgD(img_spento);
   }
   else if (music == false){
    setImgS(img_acceso);
    setImgD(img_spento_on);
   }
  }, [music]);

  return(
    <>
      <img
        className="button_impo"
        src={imgS}
        alt=""
        onClick={premiOn}
      />
      <img
        className="button_impo"
        src={imgD}
        alt=""
        onClick={premiOff}
      />
    </>
  )
};

